mapper.select:
select a.*, b.name , c.s_name  from sign_record a, course b, student c

  a.course_id = b.id  and a.stu_id = c.stu_id and

    c.stu_id=#{stuId}


    and a.course_id = #{courseId}

    and to_days(a.sign_time) = to_days(now())

select a.*, b.name , c.s_name  from sign_record a, course b, student c

  a.course_id = b.id  and a.stu_id = c.stu_id and

    c.stu_id=#{stuId}


    and a.course_id = #{courseId}

    and DATE_SUB(CURDATE(), INTERVAL 7 DAY) <= date(a.sign_time)

select a.*, b.name , c.s_name  from sign_record a, course b, student c

  a.course_id = b.id  and a.stu_id = c.stu_id and

    a.stu_id=#{stuId}


    and a.course_id = #{courseId}

    and DATE_SUB(CURDATE(), INTERVAL 30 DAY) <= date(a.sign_time)
